import React, { useRef } from 'react'
import { PrimeReactProvider, PrimeReactContext } from 'primereact/api';
import { ConfirmDialog } from 'primereact/confirmdialog';
import emailjs from '@emailjs/browser';
import { Button } from 'primereact/button';

import { Toast } from 'primereact/toast';







export const LastLeft = () => {
    const form = useRef();

    const sendEmail = (e) => {
        e.preventDefault();
        emailjs.sendForm('service_ct53cca', 'template_19vuxyw', form.current, {
            publicKey: 'NcMVkYHTwGNnv3odf',
        })
            .then((res) => {
                <>

                    {/* <Toast ref={toast} />
                    <Button onClick={show} label="Basic" /> */}
                </>
                console.log('Submitted')

            })
            .catch((err) => {
                console.log('Error sending email:', err);
            })
    }

    const MyToast = () => {
        const toast = useRef(null);

        const showToast = (severity, summary, detail) => {
            toast.current.show({ severity, summary, detail });
        };
    }

        return (
            <>
                <div className='last-left-topic'>
                    <h2>Send Message Us</h2>
                    <div className='line'></div>
                </div>
                <form ref={form} onSubmit={sendEmail} className='last-left-form'>
                    <div className='left-form-group'>
                        <input className='input-form-left' type="text" placeholder=' Your Name  ' required />
                    </div>
                    <div className='left-form-group'>
                        <input className='input-form-left' type="email" placeholder=' Your Email  ' required />
                    </div>
                    <div className='left-form-group'>
                        <input className='input-form-left' type="text" placeholder=' Subject  ' required />
                    </div>
                    <div className='left-form-group'>
                        <textarea className='input-form-left form-control' type="text" rows={5} placeholder=' Message  ' required />
                    </div>
                    <div className='last-left-button-container'>
                        <button typeof='submit' className='last-left-button' type='submit'> Send Message</button>
                    </div>
                </form>
                <Toast ref={toast} />
      <Button onClick={() => showToast('success', 'Success Message', 'Task executed successfully')}>
        Show Message
      </Button>
            </>
        )
    }
