useEffect(() => {
  const handleScroll = () => {
    if (window.scrollY >= 1000) {
      setShowButton(true);
    } else {
      setShowButton(false);
    }
  };

  window.addEventListener('scroll', handleScroll);
  return () => {
    window.removeEventListener('scroll', handleScroll);
  };
}, [showButton]);
