import React, { useState } from 'react'
import './TwoPeople.css'

export const TwoPeople = () => {
    const [activeIndex, setActiveIndex] = useState(0);
    
    const teamMembers = [
        {
            image: require('../assets/yaman.png'),
            name: "Yaman Ajlouni",
            description: "Responsible for the creation and implementation of web-related projects and maintenance."
        },
        {
            image: require('../assets/yaman.png'), // Replace with second team member image
            name: "Team Member", // Replace with actual name
            description: "Responsible for design and user experience of web projects."
        }
    ];

    return (
        <div className='people-out-container'>
            <div className='people-container' key={activeIndex}>
                <img 
                    src={teamMembers[activeIndex].image} 
                    style={{ width: 150, height: 150, borderRadius: 150 }} 
                    alt={teamMembers[activeIndex].name} 
                />
                <h2>{teamMembers[activeIndex].name}</h2>
                <p>{teamMembers[activeIndex].description}</p>
            </div>
            
            <div className='people-circle-container'>
                {teamMembers.map((_, index) => (
                    <div 
                        key={index}
                        className={`people-circle ${index === activeIndex ? 'checked' : ''}`}
                        onClick={() => setActiveIndex(index)}
                    ></div>
                ))}
            </div>
        </div>
    )
}
