import React, { useEffect, useRef, useState } from 'react'
import './Button.css'


export const Button = () => {
  const [showButton, SetShowButton] = useState(false)

  useEffect(() => {
    console.log(window.scrollY)
    if (window.scrollY >= 1000) {
      SetShowButton(true)
    }
    else SetShowButton(false)
  }, [window.scrollY,showButton])


  return (
    <div>

      {showButton && <button onClick={() => window.scrollTo(0, 0)}
        className='pi pi-arrow-up btn-all-page'>
      </button>}
    </div>
  )
}
