import React, { useCallback, useEffect, useRef, useState } from 'react'
import './Button.css'


export const Button = () => {
  const [showButton, SetShowButton] = useState(false)

  // useEffect(() => {
  //   console.log(val)
  //   if (val >= 1000) {
  //     SetShowButton(true)
  //   }
  //   else SetShowButton(false)
  // }, [val, showButton])

  const handlingScroll = useCallback(() => {
    if (window.scrollY >= 1000) {
      SetShowButton(true)
    }
    else SetShowButton(false)
  }, [showButton]);

  return (
    <div onScroll={handlingScroll}>
      {showButton && <button onClick={() => window.scrollTo(0, 0)}
        className='pi pi-arrow-up btn-all-page'>
      </button>}
    </div>
  )
}
