import React, { useState, useEffect, useRef } from 'react';
import './NavBar.css';
import { Link } from "react-router-dom";

export const NavBar = () => {
    const [showMenu, setShowMenu] = useState(false);
    const menuRef = useRef(null);

    const toggleMenu = () => {
        setShowMenu(prev => !prev);
    };

    useEffect(() => {
        const handleClickOutside = (event) => {
            if (menuRef.current && !menuRef.current.contains(event.target) && !event.target.closest('.pi-align-justify')) {
                setShowMenu(false);
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, []);

    return (
        <>
            {showMenu && <div className='overlay' onClick={() => setShowMenu(false)}></div>}
            <div className='nav-container nav'>
                <div className='left-nav'>
                    <Link className='left-nav-item' to='/'>DevFolio</Link>
                </div>
                <div className='right-button-navbar'>
                    <div className='pi pi-align-justify' onClick={toggleMenu}></div>
                </div>
                <div className='right-nav' ref={menuRef}>
                    <ul className={`navbar-details-container ${showMenu ? 'active' : ''}`}>
                        <li><a className='nav-Links' href="#Home">Home</a></li>
                        <li><a className='nav-Links' href="#About">About</a></li>
                        <li><a className='nav-Links' href="#Services">Services</a></li>
                        <li><a className='nav-Links' href="#Projects">Projects</a></li>
                        <li><a className='nav-Links' href="#Contact">Contact</a></li>
                    </ul>
                </div>
            </div>
        </>
    );
};
