import React, { useCallback, useEffect, useRef, useState } from 'react'
import './Button.css'


export const Button = () => {
  const [showButton, setShowButton] = useState(false);


  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY >= 300) {
        setShowButton(true);
      } else {
        setShowButton(false);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [showButton]);


  return (
    <div >
      {showButton && <button onClick={() => window.scrollTo(0, 0)}
        className='pi pi-arrow-up btn-all-page'>
      </button>}
    </div>
  )
}
