import React, { useRef } from 'react'
import emailjs from '@emailjs/browser';
import { Button } from 'primereact/button';
import { Toast } from 'primereact/toast';






export const LastLeft = () => {
    const form = useRef();



    const showSuccess = () => {
        form.current.show({ severity: 'success', summary: 'Success', detail: 'Message Content', life: 3000 });
    }

    const sendEmail = (e) => {
        e.preventDefault();
        emailjs.sendForm('service_ct53cca', 'template_19vuxyw', form.current, {
            publicKey: 'NcMVkYHTwGNnv3odf',
        })
            .then(() => {
                showSuccess()
            })
            .catch(() => {
                console.log('Error sending email');
            })
    }


    return (
        <>
            <Toast ref={form} />
            <div className='last-left-topic'>
                <h2>Send Message Us</h2>
                <div className='line'></div>
            </div>
            <form ref={form} onSubmit={sendEmail} className='last-left-form'>
                <div className='left-form-group'>
                    <input className='input-form-left' id='from_name' type="text" placeholder=' Your Name  ' required />
                </div>
                <div className='left-form-group'>
                    <input className='input-form-left' type="email" placeholder=' Your Email  ' required />
                </div>
                <div className='left-form-group'>
                    <input className='input-form-left' type="text" placeholder=' Subject  ' required />
                </div>
                <div className='left-form-group'>
                    <textarea className='input-form-left form-control' type="text" rows={5} placeholder=' Message  ' required />
                </div>
                <div className='last-left-button-container'>
                    <button typeof='submit' className='last-left-button' type='submit'> Send Message</button>
                </div>
            </form>
        </>
    )
}
